import React, { useState } from "react";
import Backdrop from "../component/Backdrop";
import Button from "../component/Button";
import "./Card.css";

const Card = (props) => {
  const [showModal, setShowModal] = useState(); // za iskakanje prozora

  function showModalHandler() {
    setShowModal(true);
  }

  function closeModalHandler() {
    setShowModal(false);
  }

  return (
    <div>
      <div className="buttonmidel">
        <Button onClick={showModalHandler}>Pokreni konfigurator</Button>
      </div>
      {showModal && <Backdrop onClick={closeModalHandler} />}
      {showModal && (
        <div className="modal">
            <h4>Odaberite proizvođača vozila</h4>
          <div className="dropdown-content">
          <div className="dropdown-item">
            <label>
            <input type="checkbox" id="huey" name="drone" value="huey"
          />Peugot
            </label>
          </div>
          <div className="dropdown-item">
            <label>
            <input type="checkbox" id="huey" name="drone" value="huey"
          />Toyota
            </label>
          </div>
          <div className="dropdown-item">
            <label>
            <input type="checkbox" id="huey" name="drone" value="huey"
          />Alfa Romeo
            </label>
          </div>
          <div className="dropdown-item">
            <label>
            <input type="checkbox" id="huey" name="drone" value="huey"
          />Citroen
            </label>
          </div>
          <div className="dropdown-item">
            <label>
            <input type="checkbox" id="huey" name="drone" value="huey"
          />Seat
            </label>
          </div>
          <div className="dropdown-item">
            <label>
            <input type="checkbox" id="huey" name="drone" value="huey"
          />Hyundai
            </label>
          </div>
        <Button >Dalje</Button>
        </div>
        </div>
      )}
    </div>
  );
};

export default Card;
