import React, { Fragment,useState, useEffect } from "react";
import "./Kontakt.css";
import Button from "../component/Button";

function Kontakt(props) {
    const [setdalje, setShowDalje]= useState(false); //za dalje button
    const [ime, setIme]= useState(); //za ime
    const [email, setEmil]= useState(); //za email
    const [broj, setBroj]= useState(); //za broj
    const [napomena, setNapomena]= useState(''); //za napomenu

    function nazadHandler() {
        props.setShow(true);

      }
      useEffect(() => {

      if(ime && email && broj ){
         setShowDalje(true)
      }else{
          setShowDalje(false)
      }
    }, [ime,email,broj,napomena]);

  return (
      <Fragment>
    <div className="kontakt">
      <label>
        <input
          type="text"
            onChange={(e) => setIme(e.target.value)}
          placeholder="Ime i prezime*"
          required
        />
      </label>
      <label>
        <input
          type="email"
            onChange={(e) => setEmil(e.target.value)}
          placeholder="Email*"
          required
        />
      </label>
      <label>
        <input
          type="text"
            onChange={(e) => setBroj(e.target.value)}
          placeholder="Broj mobitela*"
          required
        />
      </label>
      <label>
        <input
          type="note"
            onChange={(e) => setNapomena(e.target.value)}
          placeholder="Napomena(opcionalno)"
        />
      </label>
    </div>
      <div className="buttons">
          <Button onClick={nazadHandler}>Nazad</Button>
         {setdalje && <Button >Dalje</Button>}
        </div>
    </Fragment>
  );
}

export default Kontakt;
