import React, { useState, useEffect } from "react";
import Backdrop from "../component/Backdrop";
import Button from "../component/Button";
import "./Card.css";

const check = [
  { id: 1, value: "Peugeot", isChecked: false },
  { id: 2, value: "Audi", isChecked: false },
  { id: 3, value: "Bmw", isChecked: false },
  { id: 4, value: "Seat", isChecked: false },
  { id: 5, value: "Kia", isChecked: false },
  { id: 6, value: "Honda", isChecked: false },
];

const Card = (props) => {
  const [showModal, setShowModal] = useState();
  const [showModal2, setShowModal2] = useState(false);
  const [showche, setShowChe] = useState(false);

  const [characters, updateCharacters] = useState(check);

  function showModalHandler() {
    setShowModal(true);
  }

  function closeModalHandler() {
    setShowModal(false);
  }

  //   const handleCheckChieldElement = (event) => {

  // item.isChecked = event.target.checked;
  // updateCharacters();
  //   }
  //   };

  const setInput = (i, value) => {
      updateCharacters(
          characters.map((charak) =>
          charak.id === i ? { ...charak, isChecked: value } : { ...charak }
          )
          );

          // setShowModal2
          // characters.map((cha)=>
          // cha.isChecked === true ?
          // setShowModal2(true)
          // :
          // setShowModal2(false)
          // )

          let result = characters.find(e => e.isChecked === false )

          setShowChe(result)

        };

        const lista = characters.map((item, i) => {
      return (
          <label key={i}>
        <input
          type="checkbox"
          value={item.value}
          checked={item.isChecked}
          onChange={(e) => setInput(item.id, e.target.checked)}
          />
        {item.value}
      </label>
    );
});

useEffect(() => {

    if(showche === true){
        setShowModal2(true)
    }else{
        setShowModal2(false)
    }



  }, [characters]);

  return (
    <div>
      <div className="buttonmidel">
        {/* <Link to="/signup" className="btn btn-primary">Sign up</Link> */}
        <Button onClick={showModalHandler}>Pokreni konfigurator</Button>
      </div>
      {showModal && <Backdrop onClick={closeModalHandler} />}
      {showModal &&
        (characters ? (
          <div className="modal">
            <h4>Odaberite proizvođača vozila</h4>
            <div className="dropdown-content">
              <div className="dropdown-item">{lista}</div>
              {showModal2 && <Button>Dalje</Button>}
            </div>
          </div>
        ) : (
          <div> di </div>
        ))}
    </div>
  );
};

export default Card;
