import React, { useState } from "react";
import Backdrop from "../component/Backdrop";
import Button from "../component/Button";
import Checkbox from "../component/Checkbox";
import "./Card.css";

const Card = (props) => {
  const [showModal, setShowModal] = useState(); 
  const [showModal2, setShowModal2] = useState(false); 
  const [showche, setShowChe] = useState(false); 
  const [showche1, setShowChe1] = useState(false); 
  const [showche2, setShowChe2] = useState(false); 
  const [showche3, setShowChe3] = useState(false); 
  const [showche4, setShowChe4] = useState(false); 
  const [showche5, setShowChe5] = useState(false); 


  function showModalHandler() {
    setShowModal(true);
  }

  function closeModalHandler() {
    setShowModal(false);
  }

  function handleCheckBox() {
    if (showche === false || showche1 === false || showche2 === false || showche3 === false || showche4  === false || showche5 === false ) {

      setShowModal2(true);
    } else {
      setShowModal2(false);
    }
  }

  return (
    <div>
      <div className="buttonmidel">
        <Button onClick={showModalHandler}>Pokreni konfigurator</Button>
      </div>
      {showModal && <Backdrop onClick={closeModalHandler} />}
      {showModal && (
        <div className="modal">
          <h4>Odaberite proizvođača vozila</h4>
          <div className="dropdown-content">
            <div className="dropdown-item">
              <label>
                  <Checkbox />
              </label>
            </div>
            {showModal2 && <Button>Dalje</Button>}
          </div>
        </div>
      )}
    </div>
  );
};

export default Card;
