import React, { useState, useEffect, Fragment } from "react";
import "./Usluge.css";

const usluge = [
  {
    id: 0,
    value: "Zamjena ulja i filtera (500 kn)",
    isChecked: false,
    price: 500,
  },
  { id: 1, value: "Promjena pakni (450 kn)", isChecked: false, price: 450 },
  { id: 2, value: "Promjena guma (100 kn)", isChecked: false, price: 500 },
  {
    id: 3,
    value: "Servis klima uređaja (299 kn)",
    isChecked: false,
    price: 500,
  },
  { id: 4, value: "Balansiranje guma (50 kn)", isChecked: false, price: 500 },
  {
    id: 5,
    value: "Zamjena ulja u kočnicama (229 kn)",
    isChecked: false,
    price: 500,
  },
];

const Usluge = () => {
  const [checkusluge, updateUsluge] = useState(usluge); //izmjena usluga
  const [checkresult, updateResult] = useState([]); //izmjena usluga

  // promjena charakters array
  const setInput = (i, value) => {
    updateUsluge(
      checkusluge.map((us) =>
        us.id === i ? { ...us, isChecked: value } : { ...us }
      )
    );
  };

  //mapiranje liste prikaza
  const listausluga = checkusluge.map((item, i) => {
    return (
      <label key={i} className="labelusluge">
        <input
          type="checkbox"
          value={item.value}
          checked={item.isChecked}
          onChange={(e) => setInput(item.id, e.target.checked)}
        />
        {item.value}
      </label>
    );
  });

//   let result =checkresult.reduce((a,v) =>  a = a + v.price , 0 ).toFixed(2);

  useEffect(() => {

      checkusluge.filter((i) =>{
          if(i.isChecked === true ){
              console.log(i)
              updateResult(i)
            }
            else{
                updateResult(0)
            }
        }
        )

    }, [checkusluge]);

  return (
    <Fragment>
      <div className="usluge">{listausluga}</div>
      <div className='uslugeprice'>
          <h1>Ukupno: {checkresult} </h1>
      </div>
    </Fragment>
  );
};

export default Usluge;
