import React, { useState } from "react";
import Backdrop from "../component/Backdrop";
import Button from "../component/Button";
import Checkbox from "../component/Checkbox";
import "./Card.css";


const Card = (props) => {
  const check = [
    { id: 1, value: "Peugeot", isChecked: false },
    { id: 2, value: "Audi", isChecked: false },
    { id: 3, value: "Bmw", isChecked: false },
    { id: 4, value: "Seat", isChecked: false },
    { id: 5, value: "Kia", isChecked: false },
    { id: 6, value: "Honda", isChecked: false },
  ];
  const [showModal, setShowModal] = useState();
  const [showModal2, setShowModal2] = useState(false);
  const [showche, setShowChe] = useState(false);

  const [characters, updateCharacters ]= useState(check);


  function showModalHandler() {
    setShowModal(true);
  }

  function closeModalHandler() {
    setShowModal(false);
  }

//   function handleCheckBox() {
//     if (
//       showche === false 
//     ) {
//       setShowModal2(true);
//     } else {
//       setShowModal2(false);
//     }
//   }


  const handleCheckChieldElement = (event) => {
    const items = characters;
    items.forEach(item => {
       if (item.value === event.target.value)
       console.log(event.target.value)
          item.isChecked =  event.target.checked
    })
    updateCharacters(items)
  }



  return (
    <div>
      <div className="buttonmidel">
        <Button onClick={showModalHandler}>Pokreni konfigurator</Button>
      </div>
      {showModal && <Backdrop onClick={closeModalHandler} />}
      {showModal && (
        <div className="modal">
          <h4>Odaberite proizvođača vozila</h4>
          <div className="dropdown-content">
            <div className="dropdown-item">
              <label>
                {check.map((item) => {
                  return <Checkbox key={item.id} handleCheckChieldElement={handleCheckChieldElement} {...item} />;
                })}
              </label>
            </div>
            {showModal2 && <Button>Dalje</Button>}
          </div>
        </div>
      )}
    </div>
  );
};

export default Card;
